import React, { Component } from "react";
import axios from "axios";


export default class Home extends Component {

    state = {};

    componentDidMount() {
        const config = {
            headers: {
                "Authorization": localStorage.getItem('token')
            }
        };

        axios.get('http://localhost:9001/myprofile', config).then(
            res => {
                // console.log(res);
                this.setState({
                    user: res.data
                });
            },
            err => {
                console.log(err);
            }
        )
    }

    render() {

        if(this.state.user){
            return(
                <h2>Hi {this.user.name} </h2>
            )
        }
        return (
            <h2> Welcome to Shopping App Home Page.</h2>
        );
    }
}
