import axios from "axios";
import React, { Component } from "react";
import { Row, Col } from "react-bootstrap";

export default class Register extends Component {
    handleSubmit = e => {
        e.preventDefault();
        const data = {
            id: this.id,
            name: this.name,
            userrole: this.userrole,
            businessorshopname: this.businessorshopname,
            email: this.email,
            mobile: this.mnumber,
            password: this.password,
            dob: this.dob
        };
        // console.log(data);
        axios.post('http://localhost:9001/signup', data).then(
            res => {
                console.log(res);
            }
        ).catch(
            err => {
                console.log(err);
            }
        )

    };

    render() {
        return (
            <Row className="justify-content-md-center">
                <Col xs={5}>
                <h3>Sign Up</h3>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <label>User Role</label>
                            <select onChange={e => this.userrole = e.target.value}>
                                <option value="seller">Seller</option>
                                <option value="buyer">Buyer</option>
                            </select>
                        </div>

                        <div className="form-group">
                            <label>Name</label>
                            <input type="text" className="form-control" placeholder="Name"
                                onChange={e => this.name = e.target.value} />
                        </div>
                        <div className="form-group">
                            <label>Business</label>
                            <input type="text" className="form-control" placeholder="business or shop name"
                                onChange={e => this.businessorshopname = e.target.value} />
                        </div>



                        <br></br>
                        <button className="btn btn-primary btn-block">Sign Up</button>
                    </form>
                </Col>

            </Row>

        );
    }
}
