import axios from "axios";
import React, { Component } from "react";
import { Row, Col } from "react-bootstrap";
import { Navigate, Redirect } from "react-router-dom";


export default class Buyer extends Component {
    state = {};
    handleSubmit = e => {
        e.preventDefault();
        const data = {
            id: this.id,
            name: this.name,
            userrole: 'buyer',
            email: this.email,
            mobile: this.mnumber,
            password: this.password,
            dob: this.dob
        };
        // console.log(data);
        window.location.reload(false);
        this.setState({
            signUp: true
        });
        // axios.post('http://userservice-env.eba-thsyip3x.us-east-2.elasticbeanstalk.com/signup', data).then(
        //     res => {
        //         // console.log(res);
        //         this.setState({
        //             signUp: true
        //         });
        //         window.location.reload(false);
        //     }
        // ).catch(
        //     err => {
        //         console.log(err);
        //     }
        // )

    };

    render() {
        if (this.state.signUp) {
            return <Redirect to="/forgot" replace={true} />;
        }
        return (
            <Row className="justify-content-md-center">
                <Col xs={5}>
                    {/* <h3>Sign Up</h3> */}
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <label>User ID</label>
                            <input type="text" className="form-control" placeholder="ID"
                                onChange={e => this.id = e.target.value} />
                        </div>
                        <div className="form-group">
                            <label>Name</label>
                            <input type="text" className="form-control" placeholder="Name"
                                onChange={e => this.name = e.target.value} />
                        </div>

                        <div className="form-group">
                            <label>Email</label>
                            <input type="email" className="form-control" placeholder="Email"
                                onChange={e => this.email = e.target.value} />
                        </div>
                        <div className="form-group">
                            <label>Mobile</label>
                            <input type="text" className="form-control" placeholder="Mobile Number"
                                onChange={e => this.mnumber = e.target.value} />
                        </div>
                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Password"
                                onChange={e => this.password = e.target.value} />
                        </div>
                        <div className="form-group">
                            <label>DOB</label>
                            <input type="text" className="form-control" placeholder="Date Of Birth"
                                onChange={e => this.dob = e.target.value} />
                        </div>
                        <br></br>
                        <button className="btn btn-primary btn-block">Sign Up</button>
                    </form>
                </Col>

            </Row>

        );
    }
}
