export default class Home extends Component {

    state = {};
    constructor(props) {
        super(props);

        this.state = {
            allProductsList: []
        }
    }

    componentDidMount = () => {
        axios.get('http://localhost:9002/products/viewlist/').then(
            res => {
                console.log(res.data);
                this.setState({
                    allProductsList: res.data
                });

            },
            err => {
                console.log(err);
            }
        )
    };

    render() {
        const { allProductsList = [] } = this.state;
        return (
            <div>
                <h2> Welcome to Shopping App Home Page.</h2>
                <div class="container">



                    {
                        allProductsList.length ?
                            allProductsList.map((productList, index) => (
                                <div class="row">
                                    <div class="col card bg-light mb-4">
                                        <div class="card-header">{}</div>
                                        <div class="card-body">
                                            <h5 class="card-title">Product Price: 250</h5>
                                            <p class="card-text">
                                                <span>Seller - Seller 1</span><br></br>
                                                Description - This is proudct 1</p>
                                            <a type="button" className="btn btn-primary add-cart">
                                                <Badge color="secondary"><ShoppingCartIcon />{"Add to Cart"}</Badge>
                                            </a>
                                        </div>
                                    </div>
                                </div>

                            ))
                            :
                            (<div></div>)
                    }




                </div>
            </div>
        );
    }
}
