export default class Home extends Component {

    state = {};
    constructor(props) {
        super(props);

        this.state = {
            allProductsList: []
        }
    }

    addProductToCart = e => {
alert('Hi')
    }

    componentDidMount = () => {
        axios.get('http://localhost:9002/products/viewlist/').then(
            res => {
                console.log(res.data);
                this.setState({
                    allProductsList: res.data
                });

            },
            err => {
                console.log(err);
            }
        )
    };

    render() {
        const { allProductsList = [] } = this.state;
        return (
            <div>
                <h2> Welcome to Shopping App Home Page.</h2>
                <div class="container">



                    {
                        allProductsList.length ?
                            allProductsList.map((productList, index) => (
                                <div class="row">
                                    <div class="col card bg-light mb-4">
                                        <div class="card-header">{productList.productname}</div>
                                        <div class="card-body">
                                            <h5 class="card-title">Product Price: {productList.productprice}</h5>
                                            <p class="card-text">
                                                <span>Seller - {productList.vendoremail}</span><br></br>
                                                Product Description - {productList.productdescription}</p>
                                            <Button className="btn btn-primary add-cart" onClick={e => addProductToCart(e.target.value)}>
                                                <Badge color="secondary"><ShoppingCartIcon />{"Add to Cart"}</Badge>
                                            </Button>
                                        </div>
                                    </div>
                                </div>

                            ))
                            :
                            (<div></div>)
                    }




                </div>
            </div>
        );
    }
}
