export default class Home extends Component {

    state = {};
    constructor(props) {
        super(props);

        this.state = {
            allProductsList: []
        }
    }

    componentDidMount = () => {
        axios.get('http://localhost:9002/products/viewlist/').then(
            res => {
                console.log(res.data);
                this.setState({
                    allProductsList: res.data
                });

            },
            err => {
                console.log(err);
            }
        )
    };

    render() {
        const { allProductsList = [] } = this.state;
        return (
            <div>
                <h2> Welcome to Shopping App Home Page.</h2>
                <div class="container">




                    <div class="row">
                        <div class="col card bg-light mb-4">
                            <div class="card-header">Product 1</div>
                            <div class="card-body">
